import React from 'react'
import styled from 'styled-components'

export default styled.div`
  background-color: #f60808;
  border-radius: 50%;
  border: 1px solid #ffffff;
  width: 1rem;
  height: 1rem;
  position: absolute;
  top: -0.2rem;
  right: -1rem;
`
