export const floatNumberFormatter = {
  numeral: true,
  numeralPositiveOnly: true,
  numeralDecimalMark: '.',
  delimiter: ''
}

export const positiveIntegerFormatter = {
  numeral: true,
  numeralPositiveOnly: true,
  numeralDecimalScale: 0,
  delimiter: ''
}

export const dateFormatter = {
  date: true,
  delimiter: '/',
  datePattern: ['m', 'd', 'Y']
}
